mapper.select:
SELECT gene_id FROM fpkm
WHERE 1=1


        AND pod BETWEEN #{item.begin} AND #{item.end}


        AND endosperm BETWEEN #{item.begin} AND #{item.end}


        AND seed BETWEEN #{item.begin} AND #{item.end}


        AND embryo BETWEEN #{item.begin} AND #{item.end}


        AND axis BETWEEN #{item.begin} AND #{item.end}


        AND cotyledon BETWEEN #{item.begin} AND #{item.end}


        AND seed_coat BETWEEN #{item.begin} AND #{item.end}


        AND nodule BETWEEN #{item.begin} AND #{item.end}


        AND root_hair BETWEEN #{item.begin} AND #{item.end}


        AND root BETWEEN #{item.begin} AND #{item.end}


        AND root_tip BETWEEN #{item.begin} AND #{item.end}


        AND shoot BETWEEN #{item.begin} AND #{item.end}


        AND shoot_tip BETWEEN #{item.begin} AND #{item.end}


        AND shoot_apical_meristem BETWEEN #{item.begin} AND #{item.end}


        AND shoot_meristem BETWEEN #{item.begin} AND #{item.end}


        AND primaryleaf BETWEEN #{item.begin} AND #{item.end}


        AND petiole BETWEEN #{item.begin} AND #{item.end}


        AND leaf BETWEEN #{item.begin} AND #{item.end}


        AND trifoliate BETWEEN #{item.begin} AND #{item.end}


        AND leafbud BETWEEN #{item.begin} AND #{item.end}


        AND leaflet BETWEEN #{item.begin} AND #{item.end}


        AND cotyledons_of_seedling BETWEEN #{item.begin} AND #{item.end}


        AND seedling BETWEEN #{item.begin} AND #{item.end}


        AND flower_bud BETWEEN #{item.begin} AND #{item.end}


        AND flower BETWEEN #{item.begin} AND #{item.end}


        AND stem BETWEEN #{item.begin} AND #{item.end}


        AND stem_internode BETWEEN #{item.begin} AND #{item.end}


        AND hypocotyl BETWEEN #{item.begin} AND #{item.end}
